import React, { Component, Fragment } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { loadPubs } from '../../actions/apiActions'
import { loadOpenApis } from '../../actions/apiActions'
import { RedocStandalone } from 'redoc';

import { uploadAPI } from '../../actions/apiActions'

class ApiDisplay extends Component {
    componentDidMount() {
        //this.props.loadOpenApis(0); 
        //this.props.loadPubs(0);
    }

    constructor(props) {
        super(props);
        this.state = {
            pubid: '',
            selectedFile: null,
            loaded: 0,
        };

        console.log(this.props.match.params.id);

       // this.handleSubmit = this.handleSubmit.bind(this);
    }







    renderOpenAPI(pb) {
        return (
            <RedocStandalone specUrl="http://1forge.com/openapi.json"/>
        )
      }

    renderOpenAPIs() {
        if (this.props.pub == null) {
          return null;
        }
        return (
          <div className= "position-relative overflow-hidden p-3 p-md-5 m-md-3 text-center bg-light">
          {this.props.pub.map((pb)=>this.renderOpenAPI(pb))}
            </div>
        )

      }




}


const mapStateToProps = ({ publishers, upload }) => ({

  count: publishers.total,
  pub: publishers.publishers,
  uploading: upload.isUploading,
  completed: upload.isComplete

})


const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      uploadAPI: (apiInfo) => uploadAPI(apiInfo),
      loadPubs: (page) => loadPubs(page),
      changePage: () => push('/')
    },
    dispatch
  )

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AddApi)
