import {
UPLOAD_REQUESTED, 
UPLOAD_REQUEST_SUCCESS,
UPLOAD_REQUEST_FAILURE 
    } from '../actions/apiActions';

    const initialState = {
      isUploading: false,
      isComplete: false,
      error: null
    }

    export default (state = initialState, action) => {
      switch (action.type) {
        case  UPLOAD_REQUESTED:
          return {
            ...state,
            publishers: [],
            isLoading: true,
            error: null,
          }

        case UPLOAD_REQUEST_SUCCESS:
          return {
            ...state,
            total: action.payload.total,
            publishers: action.payload.publishers,
            isLoading: false,
            error: null,
          }

        case PUB_REQUEST_FAILURE:
          return {
            ...state,
            isLoading: false,
            error: action.payload.error,
          }

        default:
          return state
      }
    }
