import React, { Component, Fragment } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { loadOpenApis } from '../../actions/apiActions'
import {Form, Input} from 'reactstrap'
import Button from 'react-bootstrap/Button'
import FormControl from 'react-bootstrap/FormControl'
import FormCheck from 'react-bootstrap/FormCheck'

import { register } from '../../actions/apiActions'

class Register extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            name: '',
            web: '',
            contact: '',
        };


        this.handleSubmit = this.handleSubmit.bind(this);
    }



    componentDidMount() {

    }

    render() {
        return (

            <div className= "position-relative overflow-hidden p-3 p-md-5 m-md-3 text-center bg-light">
                Please register your company!

                <Form>
                <Form.Group controlId="name">
                <Form.Label>Company Name</Form.Label>
                <Form.Control placeholder="XYZ Inc." />
                <Input value = {this.state.name} className="text-muted" onChange={e=>this.handleChange(e, 'name')}>
                </Input>
                </Form.Group>
                </Form>


                <Form>
                <Form.Group controlId="poc">
                <Form.Label>Point of Contact</Form.Label>
                <Form.Control placeholder="John Smith" />
                <Form.Text value = {this.state.contact} className="text-muted" onChange={e=>this.handleChange(e, 'contact')}>
                </Form.Text>
                </Form.Group>
                </Form>


                <Form>
                <Form.Group controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
                <Form.Text value = {this.state.email} className="text-muted" onChange={e=>this.handleChange(e, 'email')}>
                We'll never share your email with anyone else.
                </Form.Text>
                </Form.Group>
                </Form>

                <Form>
                <Form.Group controlId="web">
                <Form.Label>Web address</Form.Label>
                <Form.Control  placeholder="example.com" />
                <Form.Text value = {this.state.web} className="text-muted" onChange={e=>this.handleChange(e, 'web')}>
                </Form.Text>
                </Form.Group>
                </Form>
                <Button onClick={this.submitForm.bind(this)} variant="primary" type="submit">
                Submit
                </Button>

                <form onSubmit={this.handleSubmit}>
                <label>
                    Company Name: 
                <input type="text" value={this.state.value} onChange={e=>this.handleChange(e, 'name')} />
                </label>
                <p></p>
                <label>
                    Company Website: 
                <input type="text" value={this.state.value} onChange={e=>this.handleChange(e, 'web')} />
                </label>
                <p></p>
                <label>
                    Point of Contact: 
                <input type="text" value={this.state.value} onChange={e=>this.handleChange(e, 'contact')} />
                </label>
                <p></p>
                <label>
                    Email: 
                <input type="text" value={this.state.value} onChange={e=>this.handleChange(e, 'email')} />
                </label>
                <p></p>
                <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
    submitForm() {
        console.log(this.state);
        this.props.register(this.state);
    }

    handleChange(event, field) {
        switch (field ) {
            case 'web':
                this.setState({web: event.target.value});
                break;
            case 'name':
                this.setState({name: event.target.value});
                break;
            case 'email':
                this.setState({email: event.target.value});
                break;
            case 'contact':
                this.setState({contact: event.target.value});
                break;

        }
    }
}



const mapStateToProps = ({ openapis }) => ({
  count: openapis.total,
  apis: openapis.apis,

})

const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      register: (companyInfo) => register(companyInfo),

    },
    dispatch
  )

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Register)
