import React, { Component, Fragment } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { loadOpenApis } from '../../actions/apiActions'
import {Form, Input} from 'reactstrap'
import Button from 'react-bootstrap/Button'
import FormControl from 'react-bootstrap/FormControl'
import FormCheck from 'react-bootstrap/FormCheck'

import { register } from '../../actions/apiActions'

class Register extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            name: '',
            web: '',
            contact: '',
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }



    componentDidMount() {

    }

    render() {
        return (

            <div className= "position-relative overflow-hidden p-3 p-md-5 m-md-3 text-center bg-light">
                Please register your company!

                <form onSubmit={this.handleSubmit}>
                <label>
                    Company Name:
                <input type="text" value={this.state.value} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />

                <label>
                    Company Website
                <input type="text" value={this.state.value} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />

                <label>
                    Point of Contact
                <input type="text" value={this.state.value} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />

                <label>
                    Email
                <input type="text" value={this.state.value} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
    handleSubmit() {
        console.log(this.state);
        this.props.register(this.state);
    }

    handleChange(event, field) {
        switch (field ) {
            case 'web':
                this.setState({web: event.target.value});
                break;
            case 'name':
                this.setState({name: event.target.value});
                break;
            case 'email':
                this.setState({email: event.target.value});
                break;
            case 'contact':
                this.setState({contact: event.target.value});
                break;

        }
    }
}



const mapStateToProps = ({ openapis }) => ({
  count: openapis.total,
  apis: openapis.apis,

})

const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      register: (companyInfo) => register(companyInfo),

    },
    dispatch
  )

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Register)
