import React, { Component, Fragment } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { loadPubs } from '../../actions/apiActions'
import { loadOpenApis } from '../../actions/apiActions'
import {Form, Input} from 'reactstrap'
import Button from 'react-bootstrap/Button'
import FormControl from 'react-bootstrap/FormControl'
import FormCheck from 'react-bootstrap/FormCheck'

import { register } from '../../actions/apiActions'

class AddApi extends Component {
    componentDidMount() {
        //this.props.loadOpenApis(0); 
        this.props.loadPubs(0);
      }

    constructor(props) {
        super(props);
        this.state = {
            value: '',
            name: '',
            web: '',
            contact: '',
        };


        this.handleSubmit = this.handleSubmit.bind(this);
    }





    render() {
        return (
            <Fragment>
                {this.renderOpenAPIs()}
            </Fragment>      

        )
    }

    renderOpenAPI(pb) {
        return (
            <option value={pb.id}>{pb.name}</option>
        )
      }

    renderOpenAPIs() {
        if (this.props.pub == null && this.props.pub.length == 0) {
          return null;
        }
        return (
          <div className= "position-relative overflow-hidden p-3 p-md-5 m-md-3 text-center bg-light">
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <select>
                        Company

                    {this.props.pub.map(pb=>this.renderOpenAPI(pb))}
                    </select>
                    <p></p>
                    <input type="file" name="file" onChange={this.onChangeHandler}/>

                    </label>
                    <p></p>
                    <input type="submit" value="Submit" />
                    </form>
            </div>
        )

      }



    handleSubmit() {
        console.log(this.state);
        /*
        alert(this.state.web);
        alert(this.state.name);
        alert(this.state.email);
        alert(this.state.contact);
        */


        this.props.register(this.state);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
        //alert(this.props.apis);

    }
}


const mapStateToProps = ({ publishers }) => ({

  count: publishers.total,
  pub: publishers.publishers,

})


const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      //register: (companyInfo) => register(companyInfo),
      loadPubs: (page) => loadPubs(page),
      changePage: () => push('/about-us')
    },
    dispatch
  )

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AddApi)
