import React, { Component, Fragment } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { loadOpenApis } from '../../actions/apiActions'
import {Form, Input} from 'reactstrap'
import Button from 'react-bootstrap/Button'
import FormControl from 'react-bootstrap/FormControl'
import FormCheck from 'react-bootstrap/FormCheck'

import { register } from '../../actions/apiActions'

class AddApi extends Component {

    constructor(props) {
        super(props);
        this.state = {
            value: '',
            name: '',
            web: '',
            contact: '',
        };


        this.handleSubmit = this.handleSubmit.bind(this);
    }



    componentDidMount() {

    }

    render() {
        return (

            <div className= "position-relative overflow-hidden p-3 p-md-5 m-md-3 text-center bg-light">
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Pick your favorite flavor:
                        <select value={this.state.value} onChange={this.handleChange.bind(this)}>


                        <option selected value="grapefruit">Grapefruit</option>
                        <option value="lime">Lime</option>
                        <option value="coconut">Coconut</option>
                        <option value="mango">Mango</option>
                        </select>
                    </label>
        <input type="submit" value="Submit" />
      </form>
            </div>
        )
    }

    renderOpenAPI(api) {
        return (
            <div className="bg-light col-md-6 pt-3 px-3 pt-md-5 px-md-5 text-center overflow-hidden">
              <div className="my-3 p-3">
                <h2 className="display-5">{api.name}</h2>
                <p className="lead">{api.created_by}</p>
              </div>
              <div className="bg-dark shadow-sm mx-auto api-desc">
                <div className="pt-5">
                {api.description}
                </div>

              </div>
            </div>
        )
      }

      renderOpenAPIs() {
        if (this.props.apis == null && this.props.api.length == 0) {
          return null;
        }
        return (
          <div className="row">
            {this.props.apis.map(api=>this.renderOpenAPI(api))}
          </div>
        )

      }



    handleSubmit() {
        console.log(this.state);
        /*
        alert(this.state.web);
        alert(this.state.name);
        alert(this.state.email);
        alert(this.state.contact);
        */

        this.props.register(this.state);
    }

    handleChange(event) {
        this.setState({value: event.target.value});

        /*
        switch (field ) {
            case 'web':
                this.setState({web: event.target.value});
                break;
            case 'name':
                this.setState({name: event.target.value});
                break;
            case 'email':
                this.setState({email: event.target.value});
                break;
            case 'contact':
                this.setState({contact: event.target.value});
                break;
            */

    }
}



const mapStateToProps = ({ openapis }) => ({
  //count: openapis.total,
  //apis: openapis.apis,

})

const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      register: (companyInfo) => register(companyInfo),
    },
    dispatch
  )

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AddApi)
