import React, { Component, Fragment } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { loadOpenApis } from '../../actions/apiActions'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import FormControl from 'react-bootstrap/FormControl'
import FormCheck from 'react-bootstrap/FormCheck'

class Register extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            name: '',
            web: '',
            contact: '',
        };
    }



    componentDidMount() {

    }

    render() {
        return (

            <div className= "position-relative overflow-hidden p-3 p-md-5 m-md-3 text-center bg-light">
                Please register your company!

                <Form>
                <Form.Group controlId="name">
                <Form.Label>Company Name</Form.Label>
                <Form.Control placeholder="XYZ Inc." />
                <Form.Text value = {this.state.name} className="text-muted" onChange = {this.handleChange}>
                </Form.Text>
                </Form.Group>
                </Form>


                <Form>
                <Form.Group controlId="poc">
                <Form.Label>Point of Contact</Form.Label>
                <Form.Control placeholder="John Smith" />
                <Form.Text value = {this.state.contact} className="text-muted" onChange = {this.handleChange}>
                </Form.Text>
                </Form.Group>
                </Form>


                <Form>
                <Form.Group controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
                <Form.Text value = {this.state.email} className="text-muted" onChange = {this.handleChange}>
                We'll never share your email with anyone else.
                </Form.Text>
                </Form.Group>
                </Form>

                <Form>
                <Form.Group controlId="web">
                <Form.Label>Web address</Form.Label>
                <Form.Control  placeholder="example.com" />
                <Form.Text value = {this.state.web} className="text-muted" onChange = {e=> this.handleChange(e, 'web')}>
                </Form.Text>
                </Form.Group>
                </Form>
                <Button onClick={this.submitForm.bind(this)} variant="primary" type="submit">
                Submit
                </Button>
            </div>
        )
    }
    submitForm() {
        console.log(this.state);
    }

    handleChange(event, field) {
        switch (field ) {
            case 'web':
                this.setState({web: event.target.value});
                break;
            case 'name':
                this.setState({});

        }
    }
}



const mapStateToProps = ({ openapis }) => ({
  count: openapis.total,
  apis: openapis.apis,

})

const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      loadOpenApis: (page) => loadOpenApis(page),
      changePage: () => push('/about-us')
    },
    dispatch
  )

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Register)
